SELECT BIN_TO_UUID(event_reservation_users.user_id) AS user_id, comment, url, username, icon_url FROM event_reservation_users LEFT JOIN user_profiles ON event_reservation_users.user_id = user_profiles.user_id WHERE reservation_id = UUID_TO_BIN(/*reservationID*/'aaaaaaaa-bbbb-cccc-dddd-eeeeeeeeeeee');
