SELECT BIN_TO_UUID(event_reservation_users.user_id) AS user_id, comment, url, username, icon_url, event_reservation_users.created_at FROM event_reservation_users LEFT JOIN user_profiles ON event_reservation_users.user_id = user_profiles.user_id WHERE reservation_id = UUID_TO_BIN(/*reservationId*/'aaaaaaaa-bbbb-cccc-dddd-eeeeeeeeeeee') ORDER BY event_reservation_users.created_at ASC;
